let attempts = 0;
setTimeout(() => testReadiness(), 500);
function testReadiness() {
  attempts += 1;
  if (attempts === 10) {
    alert("Unable to find window.CtChatLayer");
    return;
  }
  if (window.CtChatLayer) prepareLoginForm();
  else setTimeout(() => testReadiness(), 250);
}
function prepareLoginForm() {  
  window.CtChatLayer.onReady("Clinical_Messaging", () => {
    let logoTaps = 0;
    let tapsTimeout = null;
    const loginPage = document
      .querySelector(".login-page");
    if (!loginPage) return;
    const logo = loginPage
      .querySelector(".logo");
    const loginForm = loginPage
      .querySelector(".login-form");
    const textBoxes = loginForm
      .shadowRoot
      .querySelectorAll("ct-textbox");
    const loginButton = loginForm
      .shadowRoot
      .querySelector("ct-button");
    textBoxes.forEach(textBox => {
      const input = textBox
        .shadowRoot
        .querySelector("input");
      input.addEventListener("focus", () => {
        if (window.innerHeight < 768) hideLogo();
      });
      input.addEventListener("blur", () => {
        restoreLogo();
      });
    });
    function hideLogo() {
      logo.classList.add("vanish");
    }
    function restoreLogo() {
      logo.classList.remove("vanish");
    }
    loginForm.style.opacity = 1;
    const eventName = cti.store.env.platform.type === "device" ? "touchstart" : "click";
    logo.addEventListener(eventName, () => {
      clearTimeout(tapsTimeout);
      tapsTimeout = setTimeout(() => {
        taps = 0;
      }, 400);
      logoTaps += 1;
      if (logoTaps === 2) {
        clearTimeout(tapsTimeout);
        cti.utils.callActionflow("show_qa_page");
        logoTaps = 0;
      }
    });
  });
}
